head.script:
function earnedIncomeTotal(){
    var total = getValue(jQuery('#osuIncome').val()) +  getValue(jQuery('#nonosuIncome').val()) + getValue(jQuery('#spouseIncome').val());
    jQuery('#earnedTotal').html(total);
}

function unearnedIncomeTotal(){
    var total = getValue(jQuery('#governmentBenefits').val()) +  getValue(jQuery('#foreignBenefits').val()) + getValue(jQuery('#familySupport').val()) + getValue(jQuery('#investmentIncome').val());
    jQuery('#unearnedTotal').html(total);
}

function financialAidTotal(){
    var total = getValue(jQuery('#financialAidLevel').val()) +  getValue(jQuery('#otherScholarships').val()) + getValue(jQuery('#bankLoans').val()) + getValue(jQuery('#cashAssets').val());
    jQuery('#financialAidTotal').html(total);
}

function termBillTotal(){
    var total = getValue(jQuery('#termCharges').val()) +  getValue(jQuery('#incidentalFee').val());
    jQuery('#termBillTotal').html(total);
}

/**
 * Returns the numerical value of a field. If it is not a number, it returns 0.
 */
function getValue(text) {
    if (text == "" || isNaN(text)) {
        return 0;
    } 

    return parseInt(text);
}

/**
 * Add listeners to update the total values.
 */
jQuery(document).ready(function() {
    // update totals when the read-only form loads
    earnedIncomeTotal();
    unearnedIncomeTotal();
    financialAidTotal();
    termBillTotal();

    jQuery('#osuIncome, #nonosuIncome, #spouseIncome').change(function() {
        earnedIncomeTotal();
    });

    jQuery('#governmentBenefits, #foreignBenefits, #familySupport').change(function() {
        unearnedIncomeTotal();
    });

    jQuery('#financialAidLevel, #otherScholarships, #bankLoans, #cashAssets').change(function() {
        financialAidTotal();
    });

    jQuery('#termCharges, #incidentalFee').change(function() {
        termBillTotal();
    });
});
